import React, { Component, PropTypes } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios 						   from 'axios';
import 'font-awesome/css/font-awesome.min.css';
import 'bootstrap/js/modal.js';
import swal                     	from 'sweetalert';
import Add_data 					   from './add_data.js';
/*import Add_dataTable 				   from './add_dataTable.js';*/

class masterData extends Component {


    constructor(props) {
        super(props);
        	this.state = {
				allPosts : [],
				amenityname : "",
		}

		  this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount(){

		axios
			.get('/api/masteramenities/list')
			.then(
				(res)=>{
					console.log('res', res);
					const postsdata = res.data;
					console.log('postsdata',postsdata);
					this.setState({
						allPosts : postsdata,
					});
				}
			)
			.catch((error)=>{

				console.log("error = ",error);
				// alert("Something went wrong! Please check Get URL.");
				 });				

	}

	selectedData(data){
		this.setState({
			allPosts : data,
		})

		console.log("here full data in props ______________________________",this.state.allPosts);
	}


  		deleteAmenity(event){
  			event.preventDefault();
			var id = event.target.id;
			console.log("id",id);
			const token = '';
			const url = '/api/masteramenities/'+id ;
			const headers = {
				    "Authorization" : token,
				    "Content-Type" 	: "application/json",
				};

				axios({
					method: "DELETE",
					url : url,
					headers: headers,
					timeout: 3000,
					data: null,
				})
				.then((response)=> {
			    	console.log('delete response',response);
			    	swal("Amenity deleted successfully","", "success");

			    	axios
					.get('/api/masteramenities/list')
					.then(
						(res)=>{
							console.log('res', res);
							const postsdata = res.data;
							console.log('postsdata',postsdata);
							this.setState({
								allPosts : postsdata,
							});
						}
					)
					.catch((error)=>{

						console.log("error = ",error);
						// alert("Something went wrong! Please check Get URL.");
						 });			


				}).catch((error)=> {			    // handle error
				    console.log(error);
				});
  		}

  		editRole(event){

  			event.preventDefault();
			var id = event.target.id;
			console.log("edit id",id);

			 console.log("this.state.amenityname",this.state.amenityname);

			const formValues = {
		      "amenity"     : this.state.amenityname,
		      }

		      console.log("formValues",formValues);

		    axios.put('/api/masteramenities/'+id, formValues)
		      .then( (res)=>{
		          console.log("submit ",res);
		          swal("Amenities Updated successfully", "", "success");
		          this.state.amenityname = '';    


		          axios
				.get('/api/masteramenities/list')
				.then(
					(res)=>{
						console.log('res', res);
						const postsdata = res.data;
						console.log('postsdata',postsdata);
						this.setState({
							allPosts : postsdata,
						});
